```css
body {
  text-align: center;
  font-family: Arial, "Helvetica Neue", Helvetica, sans-serif;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: auto;
  flex-wrap: wrap;
  box-sizing: border-box;
  height: 100%;
}
.semi-donut {
  --percentage: 0;
  --fill: white;
  width: var(--width);
  height: calc(var(--width) / 2);
  position: relative;
  color: #222;
  font-size: 22px;
  font-weight: 600;
  display: flex;
  align-items: flex-end;
  justify-content: center;
  overflow: hidden;
  box-sizing: border-box;
}
.semi-donut:before {
  content: "";
  width: var(--width);
  height: calc(var(--width) / 2);
  border: calc(var(--width) / 8) solid rgba(0, 0, 0, 0.4);
  border-bottom: none;
  position: absolute;
  box-sizing: border-box;
  transform-origin: 50% 0%;
  border-radius: var(--width) var(--width) 0 0;
  left: 0;
  top: 0;
}
.semi-donut .slice {
  width: var(--width);
  height: calc(var(--width) / 2);
  border: calc(var(--width) / 8) solid var(--fill);
  border-top: none;
  position: absolute;
  transform-origin: top center;
  border-radius: 0 0 var(--width) var(--width);
  left: 0;
  top: 100%;
  z-index: 5;
  animation: 1s fillGraphAnimation ease-in;
  transform: rotate(calc(1deg * (var(--percentage) * 1.8)));
  box-sizing: border-box;
  cursor: pointer;
}
.semi-donut .slice:after {
  content: attr(data-name) " " counter(varible) "%";
  counter-reset: varible var(--percentage);
  background: var(--fill);
  box-sizing: border-box;
  border-radius: 2px;
  color: black;
  font-weight: 200;
  font-size: .8em;
  padding: 3px 5px;
  top: 0px;
  position: absolute;
  left: 0;
  transform: rotate(calc(-1deg * var(--percentage) * 1.8)) translate(-25px, 0px);
  transition: 0.2s ease-in;
  transform-origin: 0 50% 0;
  opacity: 0;
}
.semi-donut .slice:hover {
  filter: grayscale(10%);
}
.semi-donut .slice:hover:after {
  opacity: 1;
  left: 30px;
}
@keyframes fillAnimation {
  0% {
    transform: rotate(-45deg);
  }
  50% {
    transform: rotate(135deg);
  }
}
@keyframes fillGraphAnimation {
  0% {
    transform: rotate(0deg);
  }
  50% {
    transform: rotate(180deg);
  }
}
